resolution:
{
  "issue": "Vault is sealed",
  "root_cause": "Vault automatically seals itself to protect its data when it detects potential threats, such as intrusion attempts or system crashes.",
  "resolution_steps": [
    {
      "step": 1,
      "description": "Check the status of the Vault",
      "command": "vault status",
      "expected_output": {
        "Sealed": true
      }
    },
    {
      "step": 2,
      "description": "Unseal the Vault using the unseal keys. This step needs to be repeated for the number of required unseals.",
      "command": "vault operator unseal <unseal_key>",
      "expected_output": {
        "Sealed": false
      }
    }
  ],
  "optional_automation": {
    "description": "Automate the unsealing process using a script",
    "config_example": {
      "unseal_script.sh": {
        "unseal_keys": ["<unseal_key_1>", "<unseal_key_2>", "<unseal_key_3>"],
        "vault_address": "<vault_address>"
      }
