resolution:
{
  "issue": "Vault is sealed",
  "root_cause": "Vault is in a sealed state due to a restart or a configuration issue.",
  "resolution_steps": [
    {
      "step": 1,
      "description": "Check the status of the Vault",
      "command": "vault status",
      "expected_output": {"Sealed": true}
    },
    {
      "step": 2,
      "description": "Unseal the Vault using the unseal keys",
      "command": "vault operator unseal <unseal_key>",
      "expected_output": {"Sealed": false}
    }
  ],
  "recommendations": ["Always keep the unseal keys in a secure place.", "Consider setting up auto-unseal for ease of operations."]
}

{
  "issue": "Root generation initialized",
  "root_cause": "The root token generation process has been started. This could be due to a manual initiation by an operator or an automated process.",
  "resolution_steps": [
    {
      "step": 1,
      "description": "Check the status of the root token generation process",
      "command": "vault operator generate-root -status",
      "expected_output": {"status": "in progress"}
    },
    {
      "step": 2,
      "description": "If the process was not intended, cancel the root token generation",
      "command": "vault operator generate-root -cancel",
      "expected_output": {"status": "cancelled"}
    },
    {
      "step": 3,
      "description": "Verify the cancellation of the root token generation",
      "command": "vault operator generate-root -status",
      "expected_output": {"status": "not in progress"}
    }
  ],
  "recommendations": ["Always ensure that the root token generation process is initiated intentionally to avoid security risks.", "Regularly monitor the status of the root token generation process."]
}

{
  "issue": "Revoked lease",
  "root_cause": "The lease for a secret in Vault has been revoked, either manually or due to expiration.",
  "resolution_steps": [
    {
      "step": 1,
      "description": "Check the status of the lease",
      "command": "vault lease lookup <lease_id>",
      "expected_output": {
        "lease_id": "<lease_id>",
        "renewable": false,
        "expire_time": "<time>"
      }
    },
    {
      "step": 2,
      "description": "If the lease is revoked, you will need to generate a new secret and lease.",
      "command": "vault write -f <path_to_secret>",
      "expected_output": {
        "lease_id": "<new_lease_id>",
        "renewable": true,
        "expire_time": "<new_time>"
      }
    }
  ],
  "recommendations": [
    "Regularly renew your leases before they expire to prevent them from being revoked.",
    "Consider setting up automatic lease renewal."
  ]
}

{
  "issue": "Vault is unsealed",
  "root_cause": "Vault is unsealed when the necessary number of unseal keys (shares) have been provided and the unseal process has been completed.",
  "resolution_steps": [
    {
      "step": 1,
      "description": "Check the status of the Vault",
      "command": "vault status",
      "expected_output": {"Sealed": false}
    },
    {
      "step": 2,
      "description": "If the Vault is unsealed and it's not intended, you can manually seal the Vault",
      "command": "vault operator seal",
      "expected_output": {"Sealed": true}
    }
  ],
  "recommendations": ["Always ensure the Vault is sealed when not in use", "Limit the number of people who have access to unseal keys"]
}
